function setCookie(b,d,c){var a=new Date;a.setTime(a.getTime()+864E5*c);c="expires="+a.toGMTString();document.cookie=b+"="+d+"; "+c+"domain=."+document.domain+";path=/;"}function getCookie(b){b+="=";for(var d=document.cookie.split(";"),c=0;c<d.length;c++){for(var a=d[c];" "==a.charAt(0);)a=a.substring(1);if(-1!=a.indexOf(b))return a.substring(b.length,a.length)}return""}
$(document).ready(function(){if("cookie-law"==getCookie("cookie-law"))$("#cookie-banner").remove();else{$("#cookie-banner").prependTo("body");$("#cookie-banner").delay(1E3).slideDown("slow");$("#cookie-banner .accept").click(function(){setCookie("cookie-law","cookie-law",60);$("#cookie-banner").slideUp("slow")});var b=$("#cookie-banner .more-info").attr("href");0>b.indexOf("http")&&$("#cookie-banner .more-info").attr("href","http://"+b);"0"==$("#new_window").attr("value")&&$("#cookie-banner .more-info").removeAttr("target")}});