function setCookie(e,t,n){var r=new Date;r.setTime(r.getTime()+n*24*60*60*1e3);var i="expires="+r.toGMTString();document.cookie=e+"="+t+"; "+i+"domain=."+document.domain+";path=/;"}function getCookie(e){var t=e+"=";var n=document.cookie.split(";");for(var r=0;r<n.length;r++){var i=n[r];while(i.charAt(0)==" ")i=i.substring(1);if(i.indexOf(t)!=-1)return i.substring(t.length,i.length)}return""}jQuery(document).ready(function(){var e=jQuery("#cookie-banner");if(getCookie("cookie-law")=="cookie-law"){e.remove()}else{e.prependTo("body");e.delay(1e3).slideDown("slow");jQuery("#cookie-banner .accept").click(function(){setCookie("cookie-law","cookie-law",60);e.slideUp("slow")});var t=jQuery("#cookie-banner .more-info").attr("href");if(t.indexOf("http")<0)jQuery("#cookie-banner .more-info").attr("href","http://"+t);var n=jQuery("#new_window").attr("value");if(n=="0"){jQuery("#cookie-banner .more-info").removeAttr("target")}}})
